<script >
    var today = new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate());
	$('#date').datepicker({
		calendarWeeks: true,
		todayHighlight: true,
		maxDate: today,
      format: "dd/mm/yyyy",
	});
    function payment(id){
    var url = "{{ route('refunds.show', ":id") }}";
        url = url.replace(':id', id);
 
        $.ajax({
            url: url,
            type: 'get',
            data: {
               "id"      :   id,
              
            },
            dataType: "JSON",
            headers: {
              'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
            },
            success: function(response) {
               console.log(response);
               $("#user_name").val(response.user_name);
               $("#total").val(response.total);
               $("#total_paid").val(response.total_paid);
               $("#total_remaining").val(response.total_remaining);
               $("#total_payble").val(response.total_payble);
               $("#user_id").val(id);
               
               $("#paymentModal").modal("show");
               
            }
   });
}
</script>

<script>
   jQuery.validator.addMethod("validDate", function(value, element) {
        return this.optional(element) || moment(value,"DD/MM/YYYY").isValid();
    }, "Please enter a valid date in the format DD/MM/YYYY");

   $.validator.addMethod("paymentvalidation", function(value, element){
    var total_payble = $("#total_payble").val();
    var pay_amount = $("#pay_amount").val();
    var isSuccess;
    $.ajax({
        type: "get",
        url: "{{route('refund.payment.validation')}}",
        dataType: "json",
        async: false,
        data: {
            "total_payble":total_payble,
            "pay_amount":pay_amount
        },
        success: function(data)
        {
         if(data.status==false){
            isSuccess=false;
         }else{
            isSuccess=true;
         }
        }
    });
   
   return isSuccess;
  
}, 'Amount is greater than payble amount');
    $('#paymentForm').validate({
        ignore: [],
        debug: false,
        rules: {
         pay_amount: {
            required: true,
            paymentvalidation:true,
         },
         paid_date:{
            required:true,
            validDate:true,
           // date: true,
         },
        },
        messages: {
         pay_amount: {
            required: "Payble amount is required."
          },
         paid_date:{
            required: "Date is required.",
         },
         }
      });
</script>
