<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/apexcharts"></script>
<script>

$(document).ready(function(){
   var e={{$totalUsersCurrentYear}};
   var f={{$totalLoginUsersCurrentYear}};
   var g={{$totalTransactionCurrentYear}};
   var h={{$totalSaleCurrentYear}};
   getData(e,f,g,h,year=new Date().getFullYear(),month=new Date().getMonth()+1);
     
});

</script>
<script>
   function filterReportByYear(){
    
      var year=$("#year").val();
      var month=$("#month").val();
      
      $.ajax({
         url: "{{route('reports')}}",
         type:'get',
         data:{
            "_token"       : "{{ csrf_token() }}",
            "year"         :  year,
            "month"        :month,
         },
         success: function(data) {
            var i=data.totalUsersCurrentYear;
            var j=data.totalLoginUsersCurrentYear;
            var k=data.totalTransactionCurrentYear;
            var l=data.totalSaleCurrentYear;
            getData(i,j,k,l,year,month);
            
         }
      });
   }
   
</script>
<script>
  
   function getData(a,b,c,d,year,month){
    document.querySelector("#chart").innerHTML='';
   
   var options = {
          series: [{
          name: "sales",
          data: [{
            x: 'Total Users',
            y: a
          }, {
            x: 'Total Login Users',
            y: b
          }, {
            x: 'Transactions',
            y: c
          }, {
            x: 'Sales Reports',
            y: d
          }]
        }],
          chart: {
          type: 'bar',
          height: 380
        },
        xaxis: {
          type: 'category',
          group: {
            style: {
              fontSize: '10px',
              fontWeight: 700
            },
            groups: [
              { title: year, cols: 4 },
            
            ]
          }
        },
        
        };

        var chart = new ApexCharts(document.querySelector("#chart"), options);
        chart.render();
}
  

$('#year').each(function() {

var year = (new Date()).getFullYear();
var current = year;
year -= 3;
for (var i = 0; i < 6; i++) {
  if ((year+i) == current)
    $(this).append('<option selected value="' + (year + i) + '">' + (year + i) + '</option>');
  else
    $(this).append('<option value="' + (year + i) + '">' + (year + i) + '</option>');
}

})
</script>
